
export interface Input {
	type: HTMLInputTypeAttribute;
	name: string;
	label?: string;
	autocomplete?: string;
	placeholder?: string;
}

export interface Textarea {
	label?: string;
	placeholder?: string;
	rows?: number;
}

export interface Disclaimer {
	label?: string;
}

export interface Form {
	classes: any;
	inputs?: Array<Input>;
	textarea?: Textarea;
	disclaimer?: Disclaimer;
	button?: string;
	description?: string;
}